import {useContext, useState} from "react";
import {useNavigate} from "react-router";
import {AuthContext} from "../context/AuthProvider";
import {HttpHeadersContext} from "../context/HttpHeadersProvider";
import axios from "axios";


function Login() {

    const { auth, setAuth } = useContext(AuthContext);
    const { headers, setHeaders } = useContext(HttpHeadersContext);

    const navigate = useNavigate();

    const [id,setId] = useState("");
    const [pwd, setPwd] = useState("");

    const changeId = (event) =>{
        setId(event.target.value);
    }

    const changePwd = (event) =>{
        setPwd(event.target.value);
    }

    const login = async () => {

        const  req={
            id: id,
            pwd: pwd
        }

        await axios.post("http://localhost:3000/user/login", req)
            .then((resp) => {
            console.log("[Login.js] login() success :D");
            console.log(resp.data);

            alert(resp.data.id + "님, 성공적으로 로그인 되었습니다");


            // JWT 토큰 저장
            localStorage.setItem('asd_access_token',resp.data.jwt);
            localStorage.setItem('id', resp.data.id);

            setAuth(resp.data.id); // 사용자 인증 정보 (아이디 저장)
            setHeaders({"Authorization": `Bearer ${resp.data.jwt}`}); // 헤더 Authrization 필드 저장

            navigate("/asdlist");


        }).catch((err) =>{
            console.log("[Login.js login() error: < ");
            console.log(err);

            alert("⚠️ " + err.response.data);
        });
    }

    return(
      <div>
          <table className="table">
              <tbody>
              <tr>
                  <th className="col-3"> 아이디</th>
                  <td>
                      <input type="text" value={id} onChange={changeId} size="50px"/>
                  </td>
              </tr>

              <tr>
                  <th>비밀번호</th>
                  <td>
                      <input type="password" value={pwd} onChange={changePwd} size="50px" />
                  </td>
              </tr>

              </tbody>
          </table><br/>

          <div className="my-1 d-flex justify-content-center">
              <button className="btn btn-outline-secondary" onClick={login}><i className="fas fa-sign-in-alt"> </i> </button>
          </div>
      </div>
    );
}

export default Login;
